class Solution {
public:
    bool intersect(vector<int>& a, vector<int>& b){
        long long x1=a[0], y1=a[1], r1=a[2];
        long long x2=b[0], y2=b[1], r2=b[2];
        double dist = sqrtl((y2-y1)*(y2-y1) + (x2-x1)*(x2-x1));
        if(dist<=(r2+r1)) return true;
        return false;
    }

    bool bfs(unordered_map<int, vector<int>>& adj, int src, int s){
        vector<bool> visited(s, false);
        queue<int> q;
        q.push(src);
        visited[src] = true;
        while(!q.empty()){
            int f = q.front();
            q.pop();
            for(int x:adj[f]){
                if(x == 1 || x == 2) return true;
                if(!visited[x]){
                    visited[x] = true;
                    q.push(x);
                }
            }
        }
        return false;
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& circles){
        unordered_map<int, vector<int>> adj;
        for(int i=0; i<circles.size(); i++){
            for(int j=i+1; j<circles.size(); j++){
                if(intersect(circles[i], circles[j])){
                    adj[i+4].push_back(j+4);
                    adj[j+4].push_back(i+4);
                }
            }
            int x1=circles[i][0], y1=circles[i][1], r1=circles[i][2];
            if((x1-r1)<=0){
                adj[i+4].push_back(0);
                adj[0].push_back(i+4);
            }
            if((x1+r1)>=X){
                adj[i+4].push_back(2);
                adj[2].push_back(i+4);
            }
            if((y1-r1)<=0){
                adj[i+4].push_back(1);
                adj[1].push_back(i+4);
            }
            if((y1+r1)>=Y){
                adj[i+4].push_back(3);
                adj[3].push_back(i+4);
            }
        }
        if(bfs(adj, 0, 4+circles.size())) return false;
        if(bfs(adj, 3, 4+circles.size())) return false;
        return true;
    }
};
