    bool f(int i , vector<vector<int>>& cc , vector<bool> &mask){
        // cout<<i<<" ";
        int x1,x2,y1,y2,r;
        r = cc[i][2];
        x1 = cc[i][0]-r;
        x2 = cc[i][0]+r;
        y1 = cc[i][1]-r;
        y2 = cc[i][1]+r;
        // cout<<x1<<" "<<x2<<" "<<y1<<" "<<y2<<endl;
        if(x1 <= X && x2 >= X){
            if(!(y1 > Y || y2 < 0)) return true;
        }
        if(y1 <= 0 && y2 >= 0){
            if(!(x2 < 0 || x1 > X)) return true;
        }

        mask[i] = true;

        for(int j=0 ; j<n ; j++)
            if(!mask[j]){
                long long dis = 1ll*(cc[i][0] - cc[j][0])*(cc[i][0] - cc[j][0]) + 1ll*(cc[i][1] - cc[j][1])*(cc[i][1] - cc[j][1]);
                if(dis <= 1ll*(cc[i][2]+cc[j][2])*(cc[i][2]+cc[j][2]))
                    if(f(j , cc , mask)) return true;
            }

        return false;
    }
