class Solution {
public:
    bool dfs(vector<vector<int>>& adj, int s, int u, int v) {
        stack<int> st;
        vector<bool> vis(adj.size(), false);
        st.push(s);
        vis[s] = true;
        while (!st.empty()) {
            int node = st.top();
            st.pop();
            for (auto it:adj[node]) {
                if (!vis[it]) {
                    st.push(it);
                    vis[it] = true;
                }
            }
        }
        bool f1=vis[u];
        bool f2=vis[v];
        if(f1||f2)
            return true;
        return false;
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
          int n = circles.size();
          vector<vector<int>> adj(n + 4);
        for (int i = 0; i < n; ++i) {
            int x = circles[i][0], y = circles[i][1], r = circles[i][2];
            if (x <= r){ 
                adj[n].push_back(i);
                adj[i].push_back(n);
            }
            if (X - x <= r){
                adj[n + 1].push_back(i);
                adj[i].push_back(n + 1);
            }
            if (y <= r){
                adj[n + 2].push_back(i);
                adj[i].push_back(n + 2);
            }
            if (Y - y <= r){
                adj[n + 3].push_back(i);
                adj[i].push_back(n + 3);
            }
            for (int j = i + 1; j < n; ++j) {
                int x2 = circles[j][0], y2 = circles[j][1], r2 = circles[j][2];
                long long dx = static_cast<long long>(x) - x2;
                long long dy = static_cast<long long>(y) - y2;
                long long sq= dx * dx + dy * dy;
                long long rsq= static_cast<long long>(r + r2) * (r + r2);
                if (sq<=rsq) {
                    adj[i].push_back(j);
                    adj[j].push_back(i);
                }
            }
        }

          bool f1=dfs(adj,n,n+1,n+2);
        bool f2=dfs(adj,n+3,n+2,n+1);
        if(f1||f2)
            return false;
        return true;
    }
};
