class Solution {
public:
    int bfss(vector<vector<int>>& g, int ss, int t111, int t211, int n) {
        queue<int> qq;
        vector<int> visss(n + 4, 0);
        qq.push(ss);
        visss[ss] = 1;

        while (!qq.empty()) {
            int uu = qq.front();
            qq.pop();
            for (int vvv: g[uu]) {
                if (!visss[vvv]) { 
                    qq.push(vvv); 
                    visss[vvv] = 1; 
                }
            }
        } 
        return visss[t111] || visss[t211];
    }
    bool canReachCorner(int X, int Y, vector<vector<int>>& cir) {
        int ncnn = cir.size();
        vector<pair<pair<int, int>, int>> ccc(ncnn); 
        vector<vector<int>> ggg(ncnn + 4);
        for (int i = 0; i < ncnn; ++i) { 
            ccc[i] = {{cir[i][0], cir[i][1]}, cir[i][2]};
        }

        for (int i = 0; i < ncnn; ++i) {
            if (ccc[i].first.first <= ccc[i].second) {
                ggg[ncnn].push_back(i);
                ggg[i].push_back(ncnn);
            }
            if (X - ccc[i].first.first <= ccc[i].second) {
                ggg[ncnn + 2].push_back(i);
                ggg[i].push_back(ncnn + 2);
            }

            if (ccc[i].first.second <= ccc[i].second) { 
                ggg[ncnn + 1].push_back(i);
                ggg[i].push_back(ncnn + 1);
            }
            if (Y - ccc[i].first.second <= ccc[i].second) { 
                ggg[ncnn + 3].push_back(i);
                ggg[i].push_back(ncnn + 3);
            }

            for (int j=i+1; j < ncnn; ++j) {
            double d = sqrt(pow(ccc[i].first.first - ccc[j].first.first, 2) + pow(ccc[i].first.second - ccc[j].first.second, 2));
                if (ccc[i].second + ccc[j].second >= d) {
                    ggg[i].push_back(j);
                    ggg[j].push_back(i);
                }
            }
        }
        return !(bfss(ggg, ncnn, ncnn + 1, ncnn + 2, ncnn + 4) || 
                 bfss(ggg, ncnn + 3, ncnn + 2, ncnn + 1, ncnn + 4)) ;
    }
};
