class Solution {
    int bfs(vector<vector<int>> &adj, int src, int t1, int t2, int n) {
        queue<int> q;
        vector<int> vis(n + 4, 0);

        q.push(src);
        vis[src] = 1;

        while (q.size()) {
            int u = q.front();
            q.pop();
            for (auto &it : adj[u]) {
                if (!vis[it]) {
                    q.push(it);
                    vis[it] = 1;
                }
            }
        }

        if(vis[t1] || vis[t2])
            return true;
        return false;
    }
public:
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int n = circles.size();
        vector<pair<int, pair<int, int>>> plot;
        vector<vector<int>> adj(n + 4);

        for (auto &it : circles) {
            plot.push_back({it[2], {it[0], it[1]}});
        }

        for (int i = 0; i < n; ++i) {     
            if (plot[i].second.first <= plot[i].first) {
                adj[n].push_back(i);
                adj[i].push_back(n);
            }

            if (plot[i].first + plot[i].second.first >= X) {
                adj[n + 2].push_back(i);
                adj[i].push_back(n + 2);
            }

            if (plot[i].second.second <= plot[i].first) {
                adj[n + 1].push_back(i);
                adj[i].push_back(n + 1);
            }

            if (plot[i].first + plot[i].second.second >= Y) {
                adj[n + 3].push_back(i);
                adj[i].push_back(n + 3);
            }

            for (int j = i + 1; j < n; j++) {
                long long d1 = plot[i].second.first - plot[j].second.first;
                long long d2 = plot[i].second.second - plot[j].second.second;
                double t = sqrt(d1 * d1 + d2 * d2);

                int sum = plot[i].first + plot[j].first;

                if (sum < t)
                    continue;

                adj[i].push_back(j);
                adj[j].push_back(i);
            }
        }

        if(bfs(adj, n, n + 1, n + 2, n + 4))
            return 0;

        if(bfs(adj, n + 3, n + 2, n + 1, n + 4))
            return 0;

        return 1;
    }
};
