vector<int> adj[1011];
class Solution {
public:

    void dfs(int node, int par, vector<int> &vis){
        for (auto i:adj[node]){
            if (i!=par and !vis[i]){
                vis[i]=1;
                dfs(i, node, vis);
            }
        }

        return;
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& c) {
        int n=c.size();
        for (int i=0; i<n+4; i++) adj[i].clear();
        long long int xx, yy, rr;
        for (int i=0; i<n; i++){
            if (c[i][1]<=c[i][2]){
                adj[i].push_back(n);
                adj[n].push_back(i);
            }

            if (c[i][0]<=c[i][2]){
                adj[i].push_back(n+1);
                adj[n+1].push_back(i);
            }

            if (c[i][1]+c[i][2]>=Y){
                adj[i].push_back(n+2);
                adj[n+2].push_back(i);
            }

            if (c[i][0]+c[i][2]>=X){
                adj[i].push_back(n+3);
                adj[n+3].push_back(i);
            }



            for (int j=i+1; j<n; j++){
                xx=(c[i][0]-c[j][0]);
                yy=(c[i][1]-c[j][1]);
                rr=(c[i][2]+c[j][2]);
                if (xx*xx + yy*yy <= rr*rr){
                    adj[i].push_back(j);
                    adj[j].push_back(i);
                }
            }






        }

         vector<int> vis(n+4);
            for (int i=0; i<n+4; i++) vis[i]=0;

            vis[n]=1;
            dfs(n,-1, vis);

            if (vis[n+1]==1 or vis[n+2]==1) return 0;

            for (int i=0; i<n+4; i++) vis[i]=0;

            vis[n+3]=1;
            dfs(n+3,-1, vis);

            if (vis[n+2]==1 or vis[n+1]==1) return 0;

            return 1;
    }
};
