class Solution {
public:
    bool canEscape(int X, int Y, vector<vector<int>>& circles) {
        int numCircles = circles.size();
        vector<vector<int>> graph(numCircles + 4);

        for (int i = 0; i < numCircles; i++) {
            if (circles[i][0] <= circles[i][2]) {
                graph[numCircles].push_back(i);
                graph[i].push_back(numCircles);
            }
            if (X - circles[i][0] <= circles[i][2]) {
                graph[numCircles + 2].push_back(i);
                graph[i].push_back(numCircles + 2);
            }
            if (circles[i][1] <= circles[i][2]) {
                graph[numCircles + 1].push_back(i);
                graph[i].push_back(numCircles + 1);
            }
            if (Y - circles[i][1] <= circles[i][2]) {
                graph[numCircles + 3].push_back(i);
                graph[i].push_back(numCircles + 3);
            }
            for (int j = i + 1; j < numCircles; ++j) {
                double distance = sqrt(pow(circles[i][0] - circles[j][0], 2) + pow(circles[i][1] - circles[j][1], 2));
                int radiusSum = circles[i][2] + circles[j][2];
                if (radiusSum >= distance) {
                    graph[i].push_back(j);
                    graph[j].push_back(i);
                }
            }
        }

        int pathBlocked = 0;
        pathBlocked = max(pathBlocked, bfs(graph, numCircles, numCircles + 1, numCircles + 2, numCircles + 4));
        pathBlocked = max(pathBlocked, bfs(graph, numCircles + 3, numCircles + 2, numCircles + 1, numCircles + 4));

        return pathBlocked == 0;
    }

    int bfs(const vector<vector<int>>& graph, int start, int target1, int target2, int totalNodes) {
        queue<int> nodeQueue;
        vector<bool> visited(totalNodes + 4, false);

        nodeQueue.push(start);
        visited[start] = true;

        while (!nodeQueue.empty()) {
            int currentNode = nodeQueue.front();
            nodeQueue.pop();

            for (int neighbor : graph[currentNode]) {
                if (!visited[neighbor]) {
                    nodeQueue.push(neighbor);
                    visited[neighbor] = true;
                }
            }
        }

        return (visited[target1] || visited[target2]) ? 1 : 0;
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        return canEscape(X, Y, circles);
    }
};
