class Solution {
    public boolean canReachCorner(int X, int Y, int[][] c) {
        int n = c.length;
        int[] a = new int[n];
        int[][] range = new int[n][4];
        for (int i = 0; i < n; i++) {
            a[i] = i;
            range[i][0] = c[i][0] - c[i][2];
            range[i][1] = c[i][0] + c[i][2];
            range[i][2] = c[i][1] - c[i][2];
            range[i][3] = c[i][1] + c[i][2];
        }
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                long x = c[i][0] - c[j][0], y = c[i][1] - c[j][1], d = x * x + y * y, l = (long)(c[i][2] + c[j][2]), ls = l * l;
                // System.out.println(d + "," + ls);
                if (d <= ls) union(a, i, j);
            }
        }
        for (int i = 0; i < n; i++) {
            int r = find(a, i);
            range[r][0] = Math.min(range[r][0], range[i][0]);
            range[r][1] = Math.max(range[r][1], range[i][1]);
            range[r][2] = Math.min(range[r][2], range[i][2]);
            range[r][3] = Math.max(range[r][3], range[i][3]);
        }
        for (int i = 0; i < n; i++) {
            // System.out.println(i + ":" + a[i]);
            // for (int k = 0; k < 4; k++) System.out.print(range[i][k] + ",");
            // System.out.println("");
            if (range[i][0] <= 0 && range[i][1] >= X) return false;
            if (range[i][2] <= 0 && range[i][3] >= Y) return false;
            if (range[i][0] <= 0 && range[i][2] <= 0) return false;
            if (range[i][1] >= X && range[i][3] >= Y) return false;
        }
        return true;
    }

    private int find(int[] a, int i) {
        if (a[i] != i) {
            a[i] = find(a, a[i]);
        }
        return a[i];
    }

    private void union(int[] a, int i, int j) {
        // System.out.println("u" + i + "," + j);
        int p1 = find(a, i), p2 = find(a, j);
        if (p1 > p2) a[p1] = p2;
        else a[p2] = p1;
    }
}
