class Solution {
public:
    #define N 1010

    using ll=long long;

    int f[N];

    int find(int x){
        return x==f[x]? x: f[x]=find(f[x]);
    }

    #define x first
    #define y second
    using pii=pair<int, int>;

    ll Dis(pii a, pii b){
        int dx=a.x-b.x;
        int dy=a.y-b.y;
        return 1LL*dx*dx+1LL*dy*dy;
    }

    bool ci(vector<int> &fir, vector<int> &sec){
        ll D=Dis(pii(fir[0], fir[1]), pii(sec[0], sec[1]));
        ll D2=fir[2]+sec[2];
        D2*=D2;
        return D<=D2;
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& cs) {
        int n=cs.size();
        for(int i=0; i<n+4; i++) f[i]=i;

        for(int i=0; i<n; i++) for(int j=i+1; j<n; j++){
            if(ci(cs[i], cs[j])) f[find(i)]=find(j);
        }

        int idx=0;
        for(auto &c: cs){
            int x=c[0], y=c[1], r=c[2];

            if(y>Y){
                if(Dis(pii(x, y), pii(0, Y)) <= 1LL*r*r) f[find(idx)]=find(n);
            }
            else{
                if(x <= r) f[find(idx)]=find(n);
            }

            if(x>X){
                if(Dis(pii(x, y), pii(X, 0)) <= 1LL*r*r) f[find(idx)]=find(n+1);
            }
            else{
                if(y <= r) f[find(idx)]=find(n+1);
            }

            if(y>Y){
                if(Dis(pii(x, y), pii(X, Y)) <= 1LL*r*r) f[find(idx)]=find(n+2);
            }
            else{
                if(abs(X-x) <= r) f[find(idx)]=find(n+2);
            }

            if(x>X){
                if(Dis(pii(x, y), pii(X, Y)) <= 1LL*r*r) f[find(idx)]=find(n+3);
            }
            else{
                if(abs(Y-y) <= r) f[find(idx)]=find(n+3);
            }

            ++idx;
        }

        // cout<<find(n+2)<<endl;
        // cout<<find(n+3)<<endl;
        if(find(n)==find(n+1) || find(n)==find(n+2)) return false;
        if(find(n+1)==find(n+3) || find(n+2)==find(n+3)) return false;
        return true;
    }
};
