class Solution {
public:
    vector<int> adj[1005];
    bool intersect(vector<int>& c1, vector<int>& c2)
    {
        long long d1 = (c1[0]-c2[0])*1LL*(c1[0]-c2[0]) + (c1[1]-c2[1])*1LL*(c1[1]-c2[1]);

        long long d2 = (c1[2]+c2[2])*1LL*(c1[2]+c2[2]);

        return d2 >= d1;
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {

        int n = circles.size();

        for(int i=0;i<n;i++)
        {
            if ((circles[i][0]-circles[i][2])<=0 && (circles[i][1]-circles[i][2])<=0) return false;
            else if ((circles[i][0]+circles[i][2])>=X && (circles[i][1]+circles[i][2])>=Y) return false;
            else if ((circles[i][0]-circles[i][2])<=0 && (circles[i][0]+circles[i][2])>=X) return false;
            else if ((circles[i][1]-circles[i][2])<=0 && (circles[i][1]+circles[i][2])>=Y) return false;
        }

        vector<int> cl(n,0);
        vector<bool> vis(n+1,false);

        for(int i=0;i<n;i++)
        {
            if((circles[i][0]-circles[i][2])<=0) cl[i]=1;
            else if((circles[i][1]+circles[i][2])>=Y) cl[i]=2;
            else if((circles[i][0]+circles[i][2])>=X) cl[i]=3;
            else if((circles[i][1]-circles[i][2])<=0) cl[i]=4;
            for(int j=0;j<i;j++)
            {
                if (intersect(circles[i],circles[j]))
                {
                    adj[i].push_back(j);
                    adj[j].push_back(i);
                }
            }
        }

        queue<int>q;

        for(int i=0;i<n;i++) 
            if(cl[i]==1 || cl[i]==2)
            {
                vis[i]=true;
                q.push(i);
            }

        while(q.size())
        {
            int u=q.front();
            q.pop();

            for (int v:adj[u])
            {
                if(cl[v]==3 || cl[v]==4) return false;

                if(!vis[v])
                {
                    q.push(v);
                    vis[v]=true;
                }
            }
        }
        return true;

    }
};
