class Solution {
public:
    vector<int>lft, rgt, vis;
    bool dfs(int s, vector<int>g[]){
        vis[s] = 1;
        for(auto c:g[s]){
            if(rgt[c]) return true;
            if(!vis[c] && dfs(c, g)) return true;
        }
        return false;
    }
    bool canReachCorner(int X, int Y, vector<vector<int>>& c) {
        // sort(c.begin(), c.end());

        int n = c.size();
        vector<int>g[n];
        lft.resize(n), rgt.resize(n), vis.resize(n);

        bool left = true, right = true;
        for(int i=0; i<c.size(); i++){
            int x = c[i][0];
            int y = c[i][1];
            int r = c[i][2];
            if(((x+r>=X) || (y-r<=0)) && ((x-r<=0) || (y+r>=Y))) return false;
            if((x+r>=X) || (y-r<=0)){
                right = false;
                rgt[i] = true;
            }
            if((x-r<=0) || (y+r>=Y)){
                left = false;
                lft[i] = true;
            }
        }
        if(right || left) return true;
        if(c.size()==1) return true;
        int px = c[0][0], py = c[0][1], pr = c[0][2];
        // for(int i=1; i<c.size(); i++){
        //     int x = c[i][0];
        //     int y = c[i][1];
        //     int r = c[i][2];
        //     long long d = (px-x)*(px-x) + (py-y)*(py-y);

        //     if(((pr+r)*1LL*(pr+r))<d) return true;
        //     px = x;
        //     py = y;
        //     pr = r;
        // }
        for(int i=0; i<c.size(); i++){
            for(int j=0; j<c.size(); j++){
                if(i==j) continue;
                int x1 = c[i][0];
                int y1 = c[i][1];
                int r1 = c[i][2];

                int x2 = c[j][0];
                int y2 = c[j][1];
                int r2 = c[j][2];
                long long d = (x1-x2)*1LL*(x1-x2) + (y1-y2)*1LL*(y1-y2);

                if(((r1+r2)*1LL*(r1+r2))>=d){
                    g[i].push_back(j);
                    g[j].push_back(i);
                }
            }
        }

        // for(int i=0; i<n; i++) cout<<lft[i]<<" "; cout<<endl;
        // for(int i=0; i<n; i++) cout<<rgt[i]<<" "; cout<<endl;

        // for(int i=0; i<n; i++){
        //     cout<<i<<" --> ";
        //     for(auto c:g[i]) cout<<c<<" "; cout<<endl;
        // }

        for(int i=0; i<c.size(); i++){
            if(lft[i] && !vis[i]){
                if(dfs(i, g)) return false;
            }
        }


        return true;
    }
};
