class Solution:
    def canReachCorner(self, X: int, Y: int, circles: List[List[int]]) -> bool:
        c = len(circles)
        adj = defaultdict(list)
        for i in range(c):
            x, y, r = circles[i]
            if x - r <= 0 or y + r >= Y:
                adj['l'].append(i)
                adj[i].append('l')
            if x + r >= X or y - r <= 0:
                adj['r'].append(i)
                adj[i].append('r')
            for j in range(i + 1, c):
                u, v, s = circles[j]
                if r + s >= sqrt((x - u) ** 2 + (y - v) ** 2):
                    adj[i].append(j)
                    adj[j].append(i)

        q = ['l']
        seen = {'l'}
        while q:
            cur = q.pop()
            if cur == 'r':
                return False
            for nxt in adj[cur]:
                if nxt not in seen:
                    q.append(nxt)
                    seen.add(nxt)
        return True
