int mod=998244353;
ll power(ll x,ll n){if(n==1)return x;ll a=power(x,n/2)%mod;if(n&1)return (a*a*x)%mod;return (a*a )%mod;} 
vector<int>prime(int n){vector<int>a(n+1,1);a[0]=0,a[1]=0;for(int i=2;i<=n;i++){if(a[i]){for(int j=i*i;j<=n;j+=i)a[j]=0;}}vector<int>ans;for(int i=2;i<=n;i++){if(a[i]==1)ans.push_back(i);}return ans;}
vector<int>sieve(int n){vector<int>a(n+1,1);a[0]=0,a[1]=0;for(int i=2;i<=n;i++){if(a[i]){for(int j=i*i;j<=n;j+=i)a[j]=0;}}return a;}

class Solution {
public:
bool overlap(vector<int>&x,vector<int>&y){
    double a1=x[0],a2=x[1],b1=y[0],b2=y[1];
    double dis=sqrt((a1-b1)*(a1-b1)+(a2-b2)*(a2-b2));
    double r=x[2];r+=y[2];
    if(dis<=r)return 1;
    return 0;
}
    bool canReachCorner(int X, int Y, vector<vector<int>>& c) {
        vector<ll>vx0,vy0,vxx,vyy;
        vector<ll>adj[1001];
        for(int i=0;i<c.size();i++){
            vector<int>x=c[i];
            if(x[0]-x[2]<=0 and x[1]-x[2]<=0)return 0;
            if(x[0]+x[2]>=X and x[1]+x[2]>=Y)return 0;
            if(x[1]-x[2]<=0)vy0.push_back(i);
            if(x[0]-x[2]<=0)vx0.push_back(i);
            if(x[0]+x[2]>=X)vxx.push_back(i);
            if(x[1]+x[2]>=Y)vyy.push_back(i);
            for(int j=0;j<c.size();j++){
                if(overlap(c[i],c[j])){
                    adj[i].push_back(j);
                    adj[j].push_back(i);
                }
            }

        }ll n=c.size();
        map<ll,ll>mpy0,mpyy,mpx0,mpxx;
        for(int i=0;i<vy0.size();i++)mpy0[vy0[i]]++;
        for(int i=0;i<vyy.size();i++)mpyy[vyy[i]]++;
        for(int i=0;i<vx0.size();i++)mpx0[vx0[i]]++;
        for(int i=0;i<vxx.size();i++)mpxx[vxx[i]]++;
        vector<ll>vis(n,0);
        queue<ll>q;
        for(int i=0;i<vx0.size();i++){
            q.push(vx0[i]);vis[vx0[i]]=1;
        }
        while(!q.empty()){
            auto it =q.front();
            q.pop();
            if(mpxx[it])return 0;
            if(mpy0[it])return 0;
            for(auto x:adj[it]){
                if(vis[x]==0){vis[x]=1;q.push(x);}
            }
        }
        vis.resize(n,0);

        for(int i=0;i<vxx.size();i++){
            q.push(vxx[i]);vis[vxx[i]]=1;
        }
        while(!q.empty()){
            auto it =q.front();
            q.pop();
            if(mpyy[it])return 0;
            if(mpx0[it])return 0;
            for(auto x:adj[it]){
                if(vis[x]==0){vis[x]=1;q.push(x);}
            }
        }
        vis.resize(n,0);

        for(int i=0;i<vyy.size();i++){
            q.push(vyy[i]);vis[vyy[i]]=1;
        }
        while(!q.empty()){
            auto it =q.front();
            q.pop();
            if(mpy0[it])return 0;
            if(mpxx[it])return 0;
            for(auto x:adj[it]){
                if(vis[x]==0){vis[x]=1;q.push(x);}
            }
        }


        return 1;
    }
};
