/**
 * @param {number} X
 * @param {number} Y
 * @param {number[][]} circles
 * @return {boolean}
 */
var canReachCorner = function(X, Y, circles) {
    const l = circles.length;
    const parent = Array(l).fill().map((a, i) => i);
    const data = Array(l).fill().map((a, i) => {
        const [x,y,r] = circles[i];
        return [y+r, x-r, y-r, x+r];
    }); // top, left,bot, right
    const rank = Array(l);


    const find = (x) => {
        if (parent[x] !== x) {
            parent[x] = find(parent[x]);
        }
        return parent[x];
    }

    const union = (x, y) => {
        let xset = find(x);
        let yset = find(y);

        if (xset === yset) return true;

        let chosen = xset;

        if (rank[xset] < rank[yset]) {
            parent[xset] = yset;
            chosen = yset;
        } else if (rank[xset] > rank[yset]) {
            parent[yset] = xset;
        } else {
            parent[yset] = xset;
            rank[xset]++;
        }
        data[chosen] = [Math.max(data[xset][0], data[yset][0]), Math.min(data[xset][1], data[yset][1]), Math.min(data[xset][2], data[yset][2]), Math.max(data[xset][3], data[yset][3])];

        return check(...data[chosen]);
    }

    const connect = (i, j) => {
        const [xi, yi, ri] = circles[i];
        const [xj, yj, rj] = circles[j];

        if ((xi-xj)*(xi-xj) + (yi-yj)*(yi-yj) <= (ri+rj)*(ri+rj)) return true;
        return false;
    }

    const check = (top, left, bot, right) => {
        if (top >= Y && bot <= 0) return false;
        if (left <= 0 && right >= X) return false;
        if (bot <= 0 && left <= 0) return false;
        if (top >= Y && right >= X) return false;
        return true;
    }

    for (let i = 0; i < l; i++) {
        if (!check(...data[i])) return false;
        for (let j = i + 1; j < l; j++) {
            if (connect(i, j)) {
                if (!union(i,j)) return false; 
            }
        }
    }
    return true;
};
