class Solution {
public:
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
       vector<vector<int>>v(circles.size(),vector<int>(4));
       for(int i=0;i<circles.size();i++){
        if(circles[i][0]-circles[i][2]<=0){
            v[i][0]=1;
        }
        if(circles[i][0]+circles[i][2]>=X){
            v[i][1]=1;
        }
        if(circles[i][1]+circles[i][2]>=Y){
            v[i][2]=1;
        }
        if(circles[i][1]-circles[i][2]<=0){
            v[i][3]=1;
        }
        if((v[i][0]&&v[i][1])||(v[i][0]&&v[i][3])||(v[i][1]&&v[i][2])||(v[i][2]&&v[i][3])){
            return false;
        }
       } 
       for(int i=0;i<circles.size();i++){
           for(int j=0;j<circles.size();j++){
             long long l=(abs(circles[i][0]-circles[j][0]));
             long long r=abs(circles[i][1]-circles[j][1]);
             long long sum=(l*l)+(r*r);
             long long val=(long long)(circles[i][2]+circles[j][2])*(long long)(circles[i][2]+circles[j][2]);
             if(sum<=val){
              for(int k=0;k<4;k++){
                v[i][k]|=v[j][k];
              }
             }
           }
           if((v[i][0]&&v[i][1])||(v[i][0]&&v[i][3])||(v[i][1]&&v[i][2])||(v[i][2]&&v[i][3])){
            return false;
        }
       }
       return true;
    }
};
