class Solution {
public:
    bool canReachCorner(int X, int Y, const vector<vector<int>>& circles) {
        vector<pair<pair<int, int>, int>> arr;
        int bl = circles.size(), tr = circles.size() + 1, re = circles.size() + 2, te = circles.size() + 3;
        int total_nodes = circles.size() + 4;
        vector<vector<int>> adj(total_nodes);

        for (int i = 0; i < circles.size(); ++i) {
            arr.push_back({
                {circles[i][0], circles[i][1]},
                circles[i][2]
            });
        }

        for (int i = 0; i < circles.size(); ++i) {
            if (arr[i].first.first <= arr[i].second)
                 adj[bl].push_back(i), adj[i].push_back(bl);
            if (X - arr[i].first.first <= arr[i].second) 
                 adj[re].push_back(i), adj[i].push_back(re);
            if (arr[i].first.second <= arr[i].second)  
                 adj[te].push_back(i), adj[i].push_back(te);
            if (Y - arr[i].first.second <= arr[i].second)
                  adj[tr].push_back(i), adj[i].push_back(tr);
            for (int j = i + 1; j < circles.size(); ++j) {
                double distance = sqrt(
                       pow(arr[i].first.first - arr[j].first.first, 2) +  
                       pow(arr[i].first.second - arr[j].first.second, 2)
                );
                if ((arr[i].second + arr[j].second) >= distance)  adj[i].push_back(j), adj[j].push_back(i);
            }
        }

        vector<int> visited(total_nodes, 0);
        auto dfs = [&](int start, int target1, int target2, auto&& dfs_ref) -> bool {
            visited[start] = 1;
            if (start == target1 || start == target2) return true;

            for (int v : adj[start]) {
                if (!visited[v] && dfs_ref(v, target1, target2, dfs_ref))  return true;
            }
            return false;
        };

        return !(dfs(bl, te, re, dfs) or dfs(tr, re, te, dfs));
    }
};
