class Solution {
private:
    vector<int> parent;
    int getParent(int i){
        int p = i;
        while(parent[p]!=p) p = parent[p];
        while(i!=p){
            int next = parent[i];
            parent[i] = p;
            i = next;
        }
        return p;
    }
public:
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int n =circles.size();
        parent.clear();
        for(int i=0; i<n; ++i) parent.push_back(i);
        for(int i=0; i<n; ++i){
            vector<int>& circle = circles[i];
            long long x = circle[0];
            long long y = circle[1];
            long long r = circle[2];
            if(x*x+y*y<=r*r) return false;
            if((x-X)*(x-X)+(y-Y)*(y-Y)<=r*r) return false;
            for(int j=0; j<i; ++j){
                vector<int>& circle2 = circles[j];
                long long x2=circle2[0];
                long long y2=circle2[1];
                long long r2=circle2[2];
                if((x-x2)*(x-x2)+(y-y2)*(y-y2)<=(r+r2)*(r+r2)){
                    int p1 = getParent(i);
                    int p2 = getParent(j);
                    int commonP = min(p1, p2);
                    parent[p1] = commonP;
                    parent[p2] = commonP;
                }
            }
        }
        map<int, vector<int>> circleGroups;
        for(int i=0; i<n; ++i){
            circleGroups[getParent(i)].push_back(i);
        }
        for(auto z:circleGroups){
            bool touchLeft=false;
            bool touchRight=false;
            for(int i:z.second){
                vector<int>& circle = circles[i];
                long long x = circle[0];
                long long y = circle[1];
                long long r = circle[2];
                if(x-r<=0) touchLeft=true;
                if(y-r<=0) touchRight=true;
                if(x-r<=0) touchLeft=true;
                if(y-r<=0) touchRight=true;
                if(x<=X&&x+r>=X) touchRight=true;
                if(x>=X&&x-r>=X) touchRight=true;
                if(y<=Y&&y+r>=Y) touchLeft=true;
                if(y>=Y&&y-r<=Y) touchLeft=true;
            }
            if(touchLeft&&touchRight) return false;
        }
        return true;
    }
};
