#include <vector>
#include <cmath>
#include <queue>

using namespace std;

class Solution {
    bool dfs(vector<vector<int>>& g, int s, int t1, int t2, vector<bool>& vis) {
        if (s == t1 || s == t2) return true;
        vis[s] = true;
        for (int nh : g[s]) {
            if (!vis[nh] && dfs(g, nh, t1, t2, vis)) {
                return true;
            }
        }
        return false;
    }

public:
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int num_circles = circles.size();
        vector<vector<int>> g(num_circles + 4);

        for(int i = 0; i < num_circles; ++i) {
            if (circles[i][0] - circles[i][2] <= 0) {
                g[num_circles].push_back(i);
                g[i].push_back(num_circles);
            }
            if (X - circles[i][0] <= circles[i][2]) {
                g[num_circles + 2].push_back(i);
                g[i].push_back(num_circles + 2);
            }
            if (circles[i][1] - circles[i][2] <= 0) {
                g[num_circles + 1].push_back(i);
                g[i].push_back(num_circles + 1);
            }
            if (Y - circles[i][1] <= circles[i][2]) {
                g[num_circles + 3].push_back(i);
                g[i].push_back(num_circles + 3);
            }
            for (int j = i + 1; j < num_circles; ++j) {
                double d = sqrt(pow(circles[i][0] - circles[j][0], 2) + pow(circles[i][1] - circles[j][1], 2));
                if (d <= circles[i][2] + circles[j][2]) {
                    g[i].push_back(j);
                    g[j].push_back(i);
                }
            }
        }

        vector<bool> vis(num_circles + 4, false);
        bool ans = dfs(g, num_circles, num_circles + 1, num_circles + 2, vis) || 
                   dfs(g, num_circles + 3, num_circles + 2, num_circles + 1, vis);

        return !ans;
    }
};
