class Solution {
public:
    vector<vector<int>> groups;
    // int n;
    vector<int> neigh[1005];
    bool visited[1005];

    struct Record {
        int min_x = (int)(1e9+10);
        int max_x = 0;
        int min_y = (int)(1e9+10);
        int max_y = 0;
    };

    bool cross(const vector<int>& x, const vector<int>& y) {
        long long dist = (long long)(x[0] - y[0]) * (long long)(x[0] - y[0]) 
            + (long long)(x[1] - y[1]) * (long long)(x[1] - y[1]);
        return dist <= (long long)(x[2] + y[2]) * (long long)(x[2] + y[2]);
    }

    void dfs(int u, int p, Record& r, const vector<vector<int>>& circles) {
        if (visited[u])
            return;
        visited[u] = true;
        r.min_x = min(r.min_x, circles[u][0] - circles[u][2]);
        r.min_y = min(r.min_y, circles[u][1]- circles[u][2]);
        r.max_x = max(r.max_x, circles[u][0] + circles[u][2]);
        r.max_y = max(r.max_y, circles[u][1] + circles[u][2]);
        for (int v: neigh[u]) {
            if (v != p) {
                dfs(v, u, r, circles);
            }
        }
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int n = (int)circles.size();
        memset(visited, 0, sizeof(visited));
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < i; ++j) {
                if (cross(circles[i], circles[j])) {
                    neigh[i].push_back(j);
                    neigh[j].push_back(i);
                }
            }
        }

        for (int i = 0; i < n; ++i) {
            if (!visited[i])
            {
                Record r;
                dfs(i, -1, r, circles);
                // cout << "min_x = " << r.min_x << endl;
                // cout << "max_x = " << r.max_x << endl;
                // cout << "min_y = " << r.min_y << endl;
                // cout << "max_y = " << r.max_y << endl;
                if ((r.min_x <= 0 || r.max_y >= Y)
                    && (r.max_x >= X || r.min_y <= 0)) {
                    return false;        
                }
            }
        }
        return true;
    }
};
