#include <vector>
#include <queue>
#include <cmath>
#include <iostream>

class Solution {
public:
    int bfs(std::vector<std::vector<int>>& adjacencyList, int startNode, int targetNode1, int targetNode2, int totalNodes) {
        std::queue<int> nodeQueue;
        std::vector<int> visited(totalNodes + 4, 0);
        nodeQueue.push(startNode);
        visited[startNode] = 1;

        while (!nodeQueue.empty()) {
            int currentNode = nodeQueue.front();
            nodeQueue.pop();
            for (int neighborNode : adjacencyList[currentNode]) {
                if (!visited[neighborNode]) {
                    nodeQueue.push(neighborNode);
                    visited[neighborNode] = 1;
                }
            }
        }
        return visited[targetNode1] || visited[targetNode2];
    }

    bool canReachCorner(int gridWidth, int gridHeight, std::vector<std::vector<int>>& circleData) {
        std::vector<std::pair<std::pair<int, int>, int>> circles(circleData.size());
        std::vector<std::vector<int>> adjacencyList(circleData.size() + 4);
        int numberOfCircles = circleData.size();

        for (int i = 0; i < numberOfCircles; ++i) {
            circles[i].first.first = circleData[i][0];
            circles[i].first.second = circleData[i][1];
            circles[i].second = circleData[i][2];
        }

        for (int i = 0; i < numberOfCircles; ++i) {
            if (circles[i].first.first <= circles[i].second) {
                adjacencyList[numberOfCircles].push_back(i);
                adjacencyList[i].push_back(numberOfCircles);
            }
            if (gridWidth - circles[i].first.first <= circles[i].second) {
                adjacencyList[numberOfCircles + 2].push_back(i);
                adjacencyList[i].push_back(numberOfCircles + 2);
            }
            if (circles[i].first.second <= circles[i].second) {
                adjacencyList[numberOfCircles + 1].push_back(i);
                adjacencyList[i].push_back(numberOfCircles + 1);
            }
            if (gridHeight - circles[i].first.second <= circles[i].second) {
                adjacencyList[numberOfCircles + 3].push_back(i);
                adjacencyList[i].push_back(numberOfCircles + 3);
            }
            for (int j = i + 1; j < numberOfCircles; ++j) {
                double distanceBetweenCenters = sqrt(pow(circles[i].first.first - circles[j].first.first, 2) +
                                                     pow(circles[i].first.second - circles[j].first.second, 2));
                int sumOfRadii = circles[i].second + circles[j].second;
                if (sumOfRadii >= distanceBetweenCenters) {
                    adjacencyList[i].push_back(j);
                    adjacencyList[j].push_back(i);
                }
            }
        }

        int isPathBlocked = 0;
        isPathBlocked = std::max(isPathBlocked, bfs(adjacencyList, numberOfCircles, numberOfCircles + 1, numberOfCircles + 2, numberOfCircles + 4)); // Check for bottom-left corner
        isPathBlocked = std::max(isPathBlocked, bfs(adjacencyList, numberOfCircles + 3, numberOfCircles + 2, numberOfCircles + 1, numberOfCircles + 4)); // Check for top-right corner

        if (isPathBlocked) {
            return false;
        }
        return true;
    }
};
