class Solution {
public:
    bool solve(int X, int Y, vector<vector<int>>& c) {

        int nc = c.size();
        vector<vector<int>> g(nc + 4);

        for (int i = 0; i < nc; i++) {

            if (c[i][0] <= c[i][2]) {

                g[nc].push_back(i);

                g[i].push_back(nc);
            }
            if (X - c[i][0] <= c[i][2]) {

                g[nc + 2].push_back(i);

                g[i].push_back(nc + 2);
            }
            if (c[i][1] <= c[i][2]) {

                g[nc + 1].push_back(i);

                g[i].push_back(nc + 1);
            }
            if (Y - c[i][1] <= c[i][2]) {

                g[nc + 3].push_back(i);

                g[i].push_back(nc + 3);
            }
            for (int j = i + 1; j < nc; ++j) {

                double dist =
                    sqrt(pow(c[i][0] - c[j][0], 2) + pow(c[i][1] - c[j][1], 2));

                int radSum = c[i][2] + c[j][2];

                if (radSum >= dist) {

                    g[i].push_back(j);
                    g[j].push_back(i);
                }
            }
        }

        int isBlocked = 0;

        isBlocked = max(isBlocked, bfs(g, nc, nc + 1, nc + 2, nc + 4));

        isBlocked = max(isBlocked, bfs(g, nc + 3, nc + 2, nc + 1, nc + 4));

        return isBlocked == 0;
    }

    int bfs(vector<vector<int>>& g, int s, int t1, int t2, int n) {

        queue<int> q;

        vector<bool> vis(n + 4, false);

        q.push(s);

        vis[s] = true;

        while (!q.empty()) {

            int u = q.front();
            q.pop();

            for (int v : g[u]) {

                if (!vis[v]) {
                    q.push(v);
                    vis[v] = true;
                }
            }
        }

        return (vis[t1] || vis[t2] ? 1 : 0);
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {

        return solve(X, Y, circles);
    }
};
