class Solution {

    /**
     * @param Integer $X
     * @param Integer $Y
     * @param Integer[][] $circles
     * @return Boolean
     */
    function canReachCorner($X, $Y, $circles) {
        $graph = [];
        $touch = [];
        $begin = [];
        foreach ($circles as $left => $lC) {
            $lX = $lC[0];
            $lY = $lC[1];
            $lR = $lC[2];
            $touch[$left] = 0;
            if ($lX > 0 && $lX <= $lR) {
                $touch[$left] |= 1;
            }
            if (($X - $lX) <= $lR) {
                $touch[$left] |= 4;
            }
            if ($lY > 0 && $lY <= $lR) {
                $touch[$left] |= 2;
            }
            if (($Y - $lY) <= $lR) {
                $touch[$left] |= 8;
            }
            if ($touch[$left]) {
                $begin[$left] = $touch[$left];
            }
            foreach ($circles as $right => $rC) {
                if ($left == $right) {
                    continue;
                }

                $rX = $rC[0];
                $rY = $rC[1];
                $rR = $rC[2];

                $dX = abs($lX - $rX);
                $dY = abs($lY - $rY);

                if (sqrt($dX * $dX + $dY * $dY) <= $lR + $rR) {
                    $graph[$left][$right] = true;
                    $graph[$right][$left] = true;
                }
            }
        }

        foreach ($begin as $index => $t) {
            $queue = [$index];
            $visited[$index] = true;

            while ($queue != []) {
                $newQueue = [];
                foreach ($queue as $item) {
                    foreach ($graph[$item] as $next => $val) {
                        if (!$visited[$next]) {
                            $visited[$next] = true;
                            $t |= $touch[$next];
                            $newQueue[] = $next;
                        }
                    }
                }
                if (($t & 3) == 3 || ($t & 12) == 12 || ($t & 5) == 5 || ($t & 10) == 10) {
                    return false;
                }
                $queue = $newQueue;
            }
        }

        return true;
    }
}
