class Solution {
public:
    int group[1005], v[1005];
    double dist;
    queue<int> grp;
    bool checkConn(vector<vector<int>> &circles, int a, int b) {
        dist = (double)(circles[a][0] - circles[b][0]) * (double)(circles[a][0] - circles[b][0]);
        dist += (double)(circles[a][1] - circles[b][1]) * (double)(circles[a][1] - circles[b][1]);
        dist = sqrt(dist);
        // cout << dist << endl;
        if (circles[a][2] + circles[b][2] >= dist) return true;
        else return false;
    }
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int n = circles.size(), cnt = 0;
        for (int i = 0; i < n; i++) {
            if (group[i]) continue;
            int maxx = circles[i][0] + circles[i][2], maxy = circles[i][1] + circles[i][2];
            int minx = circles[i][0] - circles[i][2], miny = circles[i][1] - circles[i][2];
            // memset(v, 0, sizeof(v));
            cnt++;
            group[i] = cnt;
            grp.push(i);
            // v[i] = 1;
            while (!grp.empty()) {
                for (int j = i + 1; j < n; j++) {
                    if (group[j]) continue;
                    if (checkConn(circles, grp.front(), j)) {
                        group[j] = cnt;
                        maxx = max(maxx, circles[j][0] + circles[j][2]);
                        maxy = max(maxy, circles[j][1] + circles[j][2]);
                        minx = min(minx, circles[j][0] - circles[j][2]);
                        miny = min(miny, circles[j][1] - circles[j][2]);
                        grp.push(j);
                    }
                }
                grp.pop();
            }
            // cout << maxx << " " << maxy << " " << minx << " " << miny << " " << cnt << endl;
            if ((maxx >= X && minx <= 0) || (maxy >= Y && miny <= 0)) return false;
            if ((maxx >= X && maxy >= Y) || (minx <= 0 && miny <= 0)) return false;
        }
        return true;
    }
};
