class Solution {
public:
    bool checkCon(long long x1,long long y1, long long x2, long long y2, int dist){
        double currDi= sqrt((x2-x1)*(x2-x1)+(y2-y1)*(y2-y1));
        double cist1= dist*1.0;

        return currDi<=cist1;
    }

    bool dfs(int i, vector<int> &vis, vector<vector<int>> &adj, set<int> &end1){

        if(end1.find(i)!=end1.end()) return true;


        if(vis[i]!=-1) return false;

        vis[i]=1;

        for(auto it: adj[i]){
            if(dfs(it, vis, adj,end1)) return true;
        }

        return false;

    }
    bool canReachCorner(int X, int Y, vector<vector<int>>& circ) {
       set<int> start,end1;
        int i=0;
        for(auto it: circ){
            if(it[0]+it[2]>=X || it[1]-it[2]<=0) start.insert(i);

            if(it[0]-it[2]<=0 || it[1]+it[2]>=Y) end1.insert(i);

            ++i;
        }
        vector<vector<int>> adj(circ.size());
        for(int i=0; i<circ.size();++i){
            for(int j=0; j<circ.size();++j){
                if(i==j) continue;

                if(checkCon(circ[i][0],circ[i][1],circ[j][0],circ[j][1],circ[i][2]+circ[j][2])) adj[i].push_back(j);
            }
        }

        vector<int> vis(circ.size(),-1);

        for(auto it: start){
            if(dfs(it,vis, adj,end1)) return false;
        }

        return true;

    }
};
