class Solution {
public:
    #define ll long long
    vector<int> p,l;
    int get(int n) {
        if (p[n]==n) return n;
        return p[n]=get(p[n]); 
    }
    void uni(int a, int b){
        a=get(a); b=get(b);
        if (a==b) return;
        if (l[a]<l[b]) swap(a,b); 
        p[b]=a; 
        l[a]+=l[b]; 
    }
    ll f(ll x) {
        return x*x;
    }
    bool chk(int i, int j, vector<vector<int>>& v) {
        return f(v[i][0]-v[j][0])+f(v[i][1]-v[j][1])<=f(v[i][2]+v[j][2]); 
    }
    bool canReachCorner(int X, int Y, vector<vector<int>>& v) {
        int n=v.size(); 
        for (int i = 0; i < n+2; i++) {
            l.push_back(1);
            p.push_back(i); 
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < i; j++) {
                if (chk(i,j,v)) uni(i,j); 
            }
        }
        for (int i = 0; i < n; i++) {
            if (v[i][0]+v[i][2]>=X||v[i][1]<=v[i][2]) uni(i,n);
            if (v[i][0]<=v[i][2]||v[i][1]+v[i][2]>=Y) uni(i,n+1);
        }
        return get(n)!=get(n+1); 
    }
};
