class Solution:
    def bf(self, g: List[List[int]], s: int, t1: int, t2: int, n: int) -> bool:
        q = deque([s])
        vis = [0] * (n + 4)
        vis[s] = 1

        while q:
            u = q.popleft()
            for v in g[u]:
                if not vis[v]:
                    q.append(v)
                    vis[v] = 1

        return vis[t1] or vis[t2]

    def canReachCorner(self, X: int, Y: int, c2: List[List[int]]) -> bool:
        nc = len(c2)
        c = [((c2[i][0], c2[i][1]), c2[i][2]) for i in range(nc)]
        g = [[] for _ in range(nc + 4)]

        for i in range(nc):
            if c[i][0][0] <= c[i][1]:
                g[nc].append(i)
                g[i].append(nc)
            if X - c[i][0][0] <= c[i][1]:
                g[nc + 2].append(i)
                g[i].append(nc + 2)
            if c[i][0][1] <= c[i][1]:
                g[nc + 1].append(i)
                g[i].append(nc + 1)
            if Y - c[i][0][1] <= c[i][1]:
                g[nc + 3].append(i)
                g[i].append(nc + 3)
            for j in range(i + 1, nc):
                d = sqrt((c[i][0][0] - c[j][0][0]) ** 2 + (c[i][0][1] - c[j][0][1]) ** 2)
                if c[i][1] + c[j][1] >= d:
                    g[i].append(j)
                    g[j].append(i)

        return not (self.bf(g, nc, nc + 1, nc + 2, nc + 4) or self.bf(g, nc + 3, nc + 2, nc + 1, nc + 4))
