    bool canReachCorner(int X, int Y, vector<vector<int>>& arr) {
        int n = arr.size();
        bool ans = true;

        queue<int> q;
        vector<int> vis(n , 0);
        for (int i=0;i<n;i++){
            if (arr[i][0] - arr[i][2] > 0 )
                    continue;
                vis[i]= 1;
                q.push(i);

        }
        while(!q.empty()){
            int i = q.front(); q.pop();
            if (arr[i][1] - arr[i][2] <= 0  or arr[i][0] + arr[i][2] >= X){
                ans = false;
                break;
            }

            for (int j=0;j<n;j++){
                if (vis[j])
                        continue;
                long long dist = 1ll*(arr[i][0] - arr[j][0])*(arr[i][0] - arr[j][0]) + 1ll*(arr[i][1] - arr[j][1])*(arr[i][1] - arr[j][1]);
                if (dist <= 1ll*(arr[i][2] + arr[j][2])*(arr[i][2] + arr[j][2])){
                    vis[j] =1 ;
                    q.push(j);
                }
            }
        }
        cout << "first:" << ans << endl;

        {
