class Solution:
    def canReachCorner(self, X: int, Y: int, circles: List[List[int]]) -> bool:
        def bfs(graph, start, target1, target2, n):
            queue = deque()
            visited = [False] * (n + 4)
            queue.append(start)
            visited[start] = True
            while queue:
                u = queue.popleft()
                for v in graph[u]:
                    if not visited[v]:
                        queue.append(v)
                        visited[v] = True

            return visited[target1] or visited[target2]

        nc = len(circles)
        graph = [[] for _ in range(nc + 4)]
        c = [(circle[0], circle[1], circle[2]) for circle in circles]

        for i in range(nc):
            if c[i][0] <= c[i][2]:
                graph[nc].append(i)
                graph[i].append(nc)
            if X - c[i][0] <= c[i][2]:
                graph[nc + 2].append(i)
                graph[i].append(nc + 2)
            if c[i][1] <= c[i][2]:
                graph[nc + 1].append(i)
                graph[i].append(nc + 1)
            if Y - c[i][1] <= c[i][2]:
                graph[nc + 3].append(i)
                graph[i].append(nc + 3)
            for j in range(i + 1, nc):
                dx = c[i][0] - c[j][0]
                dy = c[i][1] - c[j][1]
                distance = (dx ** 2 + dy ** 2) ** 0.5
                if c[i][2] + c[j][2] >= distance:
                    graph[i].append(j)
                    graph[j].append(i)

        return not (bfs(graph, nc, nc + 1, nc + 2, nc + 4) or bfs(graph, nc + 3, nc + 2, nc + 1, nc + 4))
