class Solution {
public: 
    int f(int f, int x, int y, vector<vector<int>> &v, int n){
        vector<bool> vis(n+4, false);
        vis[f] = true;

        queue<int> q;
        q.push(f);

        while(!q.empty()){
            int curr = q.front();
            q.pop();

            for(int nei: v[curr]){
                if(!vis[nei]){
                    q.push(nei);
                    vis[nei] = true;
                }
            }
        }

        bool res = (vis[y]||vis[x]);
        return res;
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int n = circles.size();

        vector<pair<pair<int, int>, int>> v(n);
        for(int i=0; i<n; i++){
            v[i] = make_pair(make_pair(circles[i][0], circles[i][1]), circles[i][2]);
        }

        vector<vector<int>> adj(n+4);
        for(int i=0; i<n; i++){
            int a1 = v[i].first.first;
            int a2 = v[i].first.second;

            for(int j=i+1; j<n; j++){
                int a3 = v[j].first.first;
                int a4 = v[j].first.second;
                int a5 = v[i].second + v[j].second;

                double diff = sqrt(pow(a1 - a3, 2) + pow(a2 - a4, 2));
                if(a5 >= diff){
                    adj[j].push_back(i);
                    adj[i].push_back(j);
                }
            }

            if((X - a1) <= v[i].second){
                adj[i].push_back(n+2);
                adj[n+2].push_back(i);
            }

            if((Y - a2) <= v[i].second){
                adj[i].push_back(n+3);
                adj[n+3].push_back(i);
            }

            if(a1 <= v[i].second){
                adj[i].push_back(n);
                adj[n].push_back(i);
            }

            if(a2 <= v[i].second){
                adj[i].push_back(n+1);
                adj[n+1].push_back(i);
            }
        }

        return !(f(n+3, n+2, n+1, adj, n+4) || f(n, n+1, n+2, adj, n+4));
    }
};
