    bool canReachCorner(int X, int Y, vector<vector<int>>& nums){
        int n=nums.size();
        vector<vector<int>> adjList(n + 4);
        vector<pair<pair<int, int>, int>> c(n);
        int r=nums.size(),op=0;

        for (int i = 0; i < r; ++i) {
            c[i].ff.ff=nums[i][0];c[i].ff.ss=nums[i][1];c[i].ss=nums[i][2];
        }

         for (int i = 0; i <= r-1; ++i) {
            if (c[i].ff.ff <= c[i].ss){adjList[r].pb(i);adjList[i].pb(r);}
            if (X - c[i].ff.ff <= c[i].ss) {adjList[r + 2].pb(i);adjList[i].pb(r + 2);}
            if (c[i].ff.ss <= c[i].ss) { adjList[r + 1].pb(i);adjList[i].pb(r + 1);}
            if (Y - c[i].ff.ss <= c[i].ss) {adjList[r + 3].pb(i);adjList[i].pb(r + 3);}
            for (int j = i + 1; j < r; ++j) {
                double ddd = sqrt(pow(c[i].ff.ff - c[j].ff.ff, 2) + pow(c[i].ff.ss - c[j].ff.ss, 2));
                int rrr = c[i].ss + c[j].ss;
                if (rrr >= ddd) {adjList[i].pb(j);adjList[j].pb(i);}
            }
        }
        op = max(op, f(adjList, r, r + 1, r + 2, r + 4)); 
        op = max(op, f(adjList, r + 3, r + 2, r + 1, r + 4));
        return op ? 0 : 1;
    }
