class Solution {
public:

    int bfs(vector<vector<int>>& graph, int start, int target1, int target2, int numNodes) {
        queue<int> q;
        vector<int> visited(numNodes + 4, 0);
        q.push(start);
        visited[start] = 1;

        while (!q.empty()) {
            int node = q.front();
            q.pop();
            for (int neighbor : graph[node]) {
                if (!visited[neighbor]) {
                    q.push(neighbor);
                    visited[neighbor] = 1;
                }
            }
        }
        return visited[target1] || visited[target2];
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int numCircles = circles.size();

        vector<pair<pair<int, int>, int>> circleData(numCircles);
        vector<vector<int>> graph(numCircles + 4);

        for (int i = 0; i < numCircles; i++) {
            circleData[i] = {{circles[i][0], circles[i][1]}, circles[i][2]};
        }

        for (int i = 0; i < numCircles; i++) {
            if (circleData[i].first.first <= circleData[i].second) {
                graph[numCircles].push_back(i);
                graph[i].push_back(numCircles);
            }

            if (X - circleData[i].first.first <= circleData[i].second) {
                graph[numCircles + 2].push_back(i);
                graph[i].push_back(numCircles + 2);
            }

            if (circleData[i].first.second <= circleData[i].second) {
                graph[numCircles + 1].push_back(i);
                graph[i].push_back(numCircles + 1);
            }

            if (Y - circleData[i].first.second <= circleData[i].second) {
                graph[numCircles + 3].push_back(i);
                graph[i].push_back(numCircles + 3);
            }

            for (int j = i + 1; j < numCircles; j++) {
                double distance = sqrt(pow(circleData[i].first.first - circleData[j].first.first, 2) +
                                       pow(circleData[i].first.second - circleData[j].first.second, 2));

                if (circleData[i].second + circleData[j].second >= distance) {
                    graph[i].push_back(j);
                    graph[j].push_back(i);
                }
            }
        }

        return !(bfs(graph, numCircles, numCircles + 1, numCircles + 2, numCircles + 4) ||
                 bfs(graph, numCircles + 3, numCircles + 2, numCircles + 1, numCircles + 4));
    }
};
