class Solution {
public:
    bool canReachCorner(int w, int h, vector<vector<int>>& c) {
        auto bfs = [&](vector<vector<int>>& g, int s, int t1, int t2, int sz) -> bool {
            vector<bool> v(sz + 4, false);
            queue<int> q;
            q.push(s);
            v[s] = true;

            while (!q.empty()) {
                int n = q.front();
                q.pop();
                for (int nb : g[n]) {
                    if (!v[nb]) {
                        q.push(nb);
                        v[nb] = true;
                    }
                }
            }

            return v[t1] || v[t2];
        };

        int n = c.size();
        vector<vector<int>> g(n + 4);
        vector<vector<int>> co(n, vector<int>(3));

        for (int i = 0; i < n; ++i) {
            co[i] = c[i];
        }

        for (int i = 0; i < n; ++i) {
            if (co[i][0] <= co[i][2]) {
                g[n].push_back(i);
                g[i].push_back(n);
            }
            if (w - co[i][0] <= co[i][2]) {
                g[n + 2].push_back(i);
                g[i].push_back(n + 2);
            }
            if (co[i][1] <= co[i][2]) {
                g[n + 1].push_back(i);
                g[i].push_back(n + 1);
            }
            if (h - co[i][1] <= co[i][2]) {
                g[n + 3].push_back(i);
                g[i].push_back(n + 3);
            }
            for (int j = i + 1; j < n; ++j) {
                long long dx = co[i][0] - co[j][0];
                long long dy = co[i][1] - co[j][1];
                double dist = sqrt(dx * dx + dy * dy);
                if (co[i][2] + co[j][2] >= dist) {
                    g[i].push_back(j);
                    g[j].push_back(i);
                }
            }
        }

        return !(bfs(g, n, n + 1, n + 2, n + 4) ||
                 bfs(g, n + 3, n + 2, n + 1, n + 4));
    }
};
