#define ll long long int
#define show(a) for(auto e: a) cout<<e<<" "; cout<<endl;

class Solution {
public:
    vector<ll> vis;  
    vector<ll> adj[1005];

// map<ll,vector<ll>> mp;
vector<vector<ll>> mp;
ll x,y;

ll bfs(ll i){

    queue<ll> q;
    q.push(i);
    vis[i] = 1;

    ll xl = 1e9,yl = 1e9,xr = 0,yr = 0;

    while(!q.empty()){
        ll sz = q.size();
        while(sz--){
            auto p = q.front();q.pop();
            vis[p] = 1;

            vector<ll> tmp = mp[p];
            xl = min(xl,tmp[0]-tmp[2]);
            xr = max(xr,tmp[0]+tmp[2]);
            yl = min(yl,tmp[1]-tmp[2]);
            yr = max(yr,tmp[1]+tmp[2]);

            for(auto it:adj[p]){
                if(!vis[it]){
                    vis[it] = 1;
                    q.push(it);
                }
            }
        }
    }

    // cout<<xl<<" "<<xr<<" "<<yl<<" "<<yr<<endl;

    if((xl <= 0) and (xr >= x)){return 0;}
    if((yl <= 0) and (yr >= y)){return 0;}
    if((xl <= 0) and (yl <= 0)){return 0;}
    if((xr >= x) and (yr >= y)){return 0;}

    return 1;
}

bool canReachCorner(int X, int Y, vector<vector<int>>& v) {
    ll n = v.size();
    vis.assign(n+1,0);
    x = 1ll*X,y = 1ll*Y;
    for(ll i = 0;i<n;++i){
        mp.push_back({v[i][0],v[i][1],v[i][2]});
        for(ll j = i+1;j<n;++j){
            ll xl = v[i][0],xr = v[i][1],r1 = v[i][2]; 
            ll yl = v[j][0],yr = v[j][1],r2 = v[j][2]; 
            ll xdist = abs(yl-xl),ydist=abs(yr-xr),r = r1+r2;
            xdist *= xdist;
            ydist *= ydist;
            double dk = sqrt(1.0*(xdist+ydist));

            // cout<<i<<" "<<j<<" "<<xdist<<" "<<ydist<<" "<<r<<endl;

            if(dk <= (1.0*r)){
                adj[i].push_back(j);
                adj[j].push_back(i);
            }
        }
    }

    // for(ll i = 0;i<n;++i){
    //     show(adj[i]);
    // }

    ll ans = 1;
    for(ll i = 0;i<n;++i){
        if(!vis[i]){
            ans &= bfs(i);
        }
    }

    return ans;
}
};
