class Solution {

    public boolean canReachCorner(int X, int Y, int[][] circles) {
        int n = circles.length;
        List<List<Integer>> nl = new ArrayList<>();
        for (int i = 0; i < n + 4; i++) nl.add(new ArrayList<>());
        for (int i = 0; i < n; i++) {
            int xi = circles[i][0], yi = circles[i][1], r = circles[i][2];
            if (xi <= r) {
                nl.get(n).add(i);
                nl.get(i).add(n);
            }
            if (X - xi <= r) {
                nl.get(n + 2).add(i);
                nl.get(i).add(n + 2);
            }
            if (yi <= r) {
                nl.get(n + 1).add(i);
                nl.get(i).add(n + 1);
            }
            if (Y - yi <= r) {
                nl.get(n + 3).add(i);
                nl.get(i).add(n + 3);
            }
            for (int j = i + 1; j < n; j++) {
                int xj = circles[j][0], yj = circles[j][1], rj = circles[j][2];
                if (r + rj >= (double)Math.sqrt(Math.pow(xi - xj, 2) + Math.pow(yi - yj, 2))) {
                    nl.get(i).add(j);
                    nl.get(j).add(i);
                }
            }
        }
        boolean flag = false;
        flag = flag || bfs(nl, n, n + 1, n + 2, n + 4); 
        flag = flag || bfs(nl, n + 3, n + 2, n + 1, n + 4); 
        return !flag;
    }
    private boolean bfs(List<List<Integer>> graph, int start, int target1, int target2, int nodes) {
        Queue<Integer> queue = new LinkedList<>();
        boolean[] visited = new boolean[nodes + 4];
        queue.add(start);
        visited[start] = true;
        while (!queue.isEmpty()) {
            int u = queue.poll();
            for (int v : graph.get(u)) {
                if (!visited[v]) {
                    queue.add(v);
                    visited[v] = true;
                }
            }
        }
        return visited[target1] || visited[target2];
    }
}
