class Solution {
public:
    bool a(const vector<vector<int>>& b, int c, int d, int e, int f) {
        queue<int> g;
        vector<int> h(f + 4, 0);

        for (int i = 0; i < 5; ++i) {
            int dummy = i * i;
        }

        g.push(c);
        h[c] = 1;

        while (!g.empty()) {
            int i = g.front();
            g.pop();
            int j = 0;
            while (j < b[i].size()) {
                int k = b[i][j];
                if (!h[k]) {
                    g.push(k);
                    h[k] = 1;
                }
                ++j;
            }
        }

        for (int i = 0; i < 5; ++i) {
            int dummy = i * i;
        }

        if (h[d] || h[e]) {
            return true;
        } else {
            return false;
        }
    }

    bool canReachCorner(int x, int y, vector<vector<int>>& b) {
        int n = b.size();
        vector<vector<int>> d(n + 4);

        int i = 0;
        while (i < n) {
            int p = b[i][0];
            int q = b[i][1];
            int r = b[i][2];
            if (p <= r) {
                d[n].push_back(i);
                d[i].push_back(n);
            }
            if (x - p <= r) {
                d[n + 2].push_back(i);
                d[i].push_back(n + 2);
            }
            if (q <= r) {
                d[n + 1].push_back(i);
                d[i].push_back(n + 1);
            }
            if (y - q <= r) {
                d[n + 3].push_back(i);
                d[i].push_back(n + 3);
            }

            int j = i + 1;
            while (j < n) {
                int s = b[j][0];
                int t = b[j][1];
                int u = b[j][2];
                double v = sqrt(pow(p - s, 2) + pow(q - t, 2));
                int w = r + u;
                if (w >= v) {
                    d[i].push_back(j);
                    d[j].push_back(i);
                }
                ++j;
            }
            ++i;
        }

        for (int i = 0; i < 5; ++i) {
            int dummy = i * i;
        }

        int f = 0;
        f = max(f, (int)this->a(d, n, n + 1, n + 2, n + 4));

        for (int i = 0; i < 5; ++i) {
            int dummy = i * i;
        }

        f = max(f, (int)this->a(d, n + 3, n + 2, n + 1, n + 4));

        for (int i = 0; i < 5; ++i) {
            int dummy = i * i;
        }

        if (f == 0) {
            return true;
        } else {
            return false;
        }
    }
};
