#define fi first
#define se second
#define pb push_back

using namespace std;

class Solution {
public:
    bool bfs(vector<vector<int>>& adj, int s, int t1, int t2, int n) {
        queue<int> q;
        vector<bool> vis(n + 1, false);
        q.push(s);
        vis[s] = true;
        while (!q.empty()) {
            int u = q.front();
            q.pop();
            for (int v : adj[u]) {
                if (!vis[v]) {
                    q.push(v);
                    vis[v] = true;
                }
            }
        }
        return vis[t1] || vis[t2];
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int n = circles.size();
        vector<pair<pair<int, int>, int>> c(circles.size());
        vector<vector<int>> adj(circles.size() + 4); 

        for (int i = 0; i < circles.size(); ++i) {
            c[i] = {{circles[i][0], circles[i][1]}, circles[i][2]};
        }

        for (int i = 0; i < circles.size(); ++i) {
            if (Y - c[i].fi.se <= c[i].se) {
                adj[circles.size() + 3].pb(i); 
                adj[i].pb(circles.size() + 3);
            }
            if (X - c[i].fi.fi <= c[i].se) {
                adj[circles.size() + 2].pb(i);
                adj[i].pb(circles.size() + 2);
            }
            if (c[i].fi.fi <= c[i].se) {
                adj[n].pb(i); 
                adj[i].pb(circles.size());
            }
            if (c[i].fi.se <= c[i].se) {
                adj[circles.size() + 1].pb(i); 
                adj[i].pb(circles.size()+ 1);
            }
            for (int j = i + 1; j < circles.size(); ++j) {
                if (c[i].se + c[j].se >= sqrt(pow(c[i].fi.fi - c[j].fi.fi, 2) + pow(c[i].fi.se - c[j].fi.se, 2))) {
                    adj[i].pb(j);
                    adj[j].pb(i);
                }
            }
        }
        return !(bfs(adj, n, n + 1, n + 2, n + 4) || bfs(adj, n + 3, n + 2, n + 1, n + 4));
    }
};
