#include <vector>
#include <cmath>

class DSU {
public:
    std::vector<int> par;
    std::vector<int> siz;

    DSU(int n) {
        par.resize(n);
        siz.resize(n, 1);
        for (int i = 0; i < n; ++i) par[i] = i;
    }

    int findPar(int u) {
        if (par[u] == u) return u;
        else return par[u] = findPar(par[u]);
    }

    void unionBySiz(int u, int v) {
        u = findPar(u);
        v = findPar(v);
        if (u == v) return;
        if (siz[u] > siz[v]) {
            par[v] = u;
            siz[u] += siz[v];
        } else {
            par[u] = v;
            siz[v] += siz[u];
        }
    }
};

class Solution {
public:
    bool canReachCorner(int x, int y, std::vector<std::vector<int>>& circles) {
        int n = circles.size() + 4;
        DSU ds(n);
        // top-3, bot-2, left-0, right-1
        for (int i = 0; i < circles.size(); ++i) {
            int left = circles[i][0] - circles[i][2];
            int right = circles[i][0] + circles[i][2];
            int bot = circles[i][1] - circles[i][2];
            int top = circles[i][1] + circles[i][2];

            if (left <= 0) ds.unionBySiz(0, i + 4);
            if (right >= x) ds.unionBySiz(1, i + 4);
            if (top >= y) ds.unionBySiz(3, i + 4);
            if (bot <= 0) ds.unionBySiz(2, i + 4);

            for (int j = 0; j < circles.size(); ++j) {
                if (i == j) continue;
                int x1 = circles[i][0];
                int y1 = circles[i][1];
                int r1 = circles[i][2];

                int x2 = circles[j][0];
                int y2 = circles[j][1];
                int r2 = circles[j][2];

                long long dx = x2 - x1;
                long long dy = y2 - y1;
                long long radii_sum = r1 + r2;

                if (dx * dx + dy * dy <= radii_sum * radii_sum) {
                    ds.unionBySiz(i + 4, j + 4);
                }
            }
        }

        if (ds.findPar(0) == ds.findPar(1) || ds.findPar(2) == ds.findPar(3) || ds.findPar(3) == ds.findPar(1) || ds.findPar(0) == ds.findPar(2))
            return false;
        return true;
    }
};
