class Solution:
    def canReachCorner(self, X: int, Y: int, circles: List[List[int]]) -> bool:
        circles = list(set((x,y,z) for x,y,z in circles))
        r=list(range(len(circles)))
        setsize = list(range(len(circles)))

        def touchesLeft(x,y,r):
            if (x)**2 + (y-Y)**2 <= r**2: return True
            if (x)**2 + (y)**2 <= r**2: return True
            if 0<=y<=Y and abs(x) <= r: return True
            return False

        def touchesTop(x,y,r):
            if (x)**2 + (y-Y)**2 <= r**2: return True
            if (x-X)**2 + (y-Y)**2 <= r**2: return True
            if 0<=x<=X and abs(y-Y) <= r: return True
            return False

        def touchesBottom(x,y,r):
            if (x)**2 + (y)**2 <= r**2: return True
            if (x-X)**2 + (y)**2 <= r**2: return True
            if 0<=x<=X and abs(y) <= r: return True
            return False

        def touchesRight(x,y,r):
            if (x-X)**2 + (y-Y)**2 <= r**2: return True
            if (x-X)**2 + (y)**2 <= r**2: return True
            if 0<=y<=Y and abs(x-X) <= r: return True
            return False


        touchesLeftOrTop = {i:  touchesLeft(x,y,r) or touchesTop(x,y,r)  for i,(x,y,r) in enumerate(circles )}
        touchesRightOrBottom = {i:  touchesRight(x,y,r) or touchesBottom(x,y,r)  for i,(x,y,r) in enumerate(circles) }

        def find(x):
            if x!=r[x]:
                r[x]=find(r[x])
            return r[x]

        def uni(x,y):
            x,y=find(x),find(y)
            if x==y: return
            if setsize[x]<setsize[y]: x,y=y,x
            setsize[x] += setsize[y]
            touchesLeftOrTop[x] = touchesLeftOrTop[x] or touchesLeftOrTop[y]
            touchesRightOrBottom[x] = touchesRightOrBottom[x] or touchesRightOrBottom[y]
            r[y]=x
            return

        for i in range(len(circles)):
            x1,y1,r1=circles[i]
            for j in range(i,len(circles)):
                x2,y2,r2=circles[j]
                if sqrt((x1-x2)**2 + (y1-y2)**2) <= (r1+r2): uni(i,j)

        for root in r:
            if touchesLeftOrTop[root] and touchesRightOrBottom[root]: return False

        return True
