class Solution {
public:
    using pii = pair<int, int>;
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& qu) {
        set<int> in;
        for(int i = 0; i < n; i++){
            in.insert(i);
        }
        int q = qu.size();
        vector<int> ans;
        for(int i = 0; i < q; i++){
            int l = qu[i][0], r = qu[i][1];
            vector<int> got;
            while(true){
                auto it = in.lower_bound(l);
                if(it == in.end() || *it >= r) break;
                got.push_back(*it);
                in.erase(*it);
            }
            if(!got.empty()){
                in.insert(got[0]);
            }
            ans.push_back(in.size() - 1);
        }
        return ans;
    }
};
