class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int fa[n];
        int m = queries.size();
        set<int> s;
        for(int i=0;i<n;i++){
            s.insert(i);
        }
        vector<int> ans(m);
        for(int i=0;i<m;i++){
            int u = queries[i][0],v=queries[i][1];
            auto itu=s.find(u),itv=s.find(v);
            if(itu!=s.end()&&itv!=s.end()){
                s.erase(++itu,itv);
            }
            ans[i]=s.size()-1;
        }

        return ans;
    }
};
