class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for (int i = 0; i < n; i++)
        {
            st.insert(i);
        }

        vector<int> ans;
        for (auto& q : queries)
        {
            int u = q[0], v = q[1];
            auto iter = st.upper_bound(u);
            while (iter != st.end())
            {
                if (*iter >= v)
                    break;
                st.erase(iter++);
            }

            ans.emplace_back(st.size() - 1);
        }

        return ans;
    }
};
