#include <vector>
#include <set>
using namespace std;

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
         set<int> st;
        vector<int> dist(n, 0);

        for (int i = 1; i < n-1; ++i) 
        {
            dist[i] = dist[i - 1] + 1;
        }
        for (int i = 0; i < n; i++) {
            st.insert(i);
        }

        vector<int> answer;

        for (vector<int>& query : queries) {
            int s = query[0];
            int e = query[1];

            auto lowerb = st.lower_bound(s + 1);
            auto uperb = st.lower_bound(e);

            st.erase(lowerb, uperb);
            answer.push_back(st.size() - 1);
        }
        return answer;
    }
};
