class Solution {
public:

   vector<int> shortestDistanceAfterQueries(int aakar, vector<vector<int>>& prashna) {

        vector<int> doori(aakar, 0);
        iota(doori.begin(), doori.end(), 0);


        set<int> sthaan;
        for (int i = 0; i < aakar; ++i) {
            sthaan.insert(i);
        }


        vector<int> parinaam;


        for (const auto& pras : prashna) {
            int aaram = pras[0];
            int ant = pras[1];


            vector<int> mitaneKeLiye;
            for (auto it = sthaan.lower_bound(aaram + 1); it != sthaan.lower_bound(ant); ++it) {
                mitaneKeLiye.push_back(*it);
            }


            for (int maan : mitaneKeLiye) {
                sthaan.erase(maan);
            }


            parinaam.push_back(sthaan.size() - 1);
        }

        return parinaam;
    }
};
