using ll = long long;
using pll = pair<ll, ll>;
template <class T>
using vec = vector<T>;

#define FOR(i, s, e) for (ll i = (ll)s; i < (ll)e; i++)
#define CFOR(i, s, e) for (ll i = (ll)s; i <= (ll)e; i++)
#define TRAV(a, c) for (const auto &a : c)
#define dbg(x) cerr << "ln" << __LINE__ << ": " << #x << " = " << x << endl

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<pll> ss;
        FOR(i, 0, n) ss.insert({i, i});
        vec<int> ans;
        TRAV(e, queries) {
            auto it = ss.upper_bound({e[0], LLONG_MAX});
            it--;
            if (it->first <= e[0] && e[1] - 1 <= it->second) {
                ans.push_back(ss.size() - 1);
                continue;
            }
            while (it != ss.end() && it->second <= e[1] - 1) {
                it = ss.erase(it);
            }
            ss.insert({e[0], e[1] - 1});
            ans.push_back(ss.size() - 1);
        }
        return ans;
    }
};
