class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for(int i = 0; i < n; i++){
            s.insert(i);
        }
        vector<int> ans(queries.size());
        for(int i = 0; i < queries.size(); i++){
            int next = queries[i][0] + 1;
            while(next < queries[i][1]){
                auto iter = s.lower_bound(next);
                next = *iter;
                if(next >= queries[i][1]){
                    break;
                }
                s.erase(iter);
                next++;
            }
            ans[i] = s.size() - 1;
        }
        return ans;
    }
};
