class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int>s;
        for(int i=0;i<n;i++)s.insert(i);
        vector<int>ans;
        for(auto&q:queries){
            int l=q[0],r=q[1];
            while(true){
                auto it=s.upper_bound(l);
                if(it==s.end() or *it>=r)break;
                s.erase(it);
            }
            ans.push_back(s.size()-1);
        }
        return ans;
    }
};
