from sortedcontainers import SortedList
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        ans = []
        lis = SortedList(range(n))
        for u, v in queries:
            if u in lis and v in lis:
                l = lis.index(u)
                r = lis.index(v)
                removes = [lis[i] for i in range(l + 1, r)]
                for r in removes:
                    lis.discard(r)
            ans.append(len(lis) - 1)
        return ans
