class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        set<int>s;
        int ta[100005] = {0};
        for(int i = 0;i <= n-2;i++){
            s.insert(i);
            ta[i] = i+1;
        }
        vector<int >res;
        for(auto x:q){
            int fi = x[0],se = x[1];
            auto it = s.lower_bound(fi);
            if(it == s.end()){
                res.push_back(s.size());
                continue;
            }else if((*it) != fi){
                res.push_back(s.size());
                continue;
            }else{
                if(ta[*it] < se){
                    vector<int>tmp;
                    for(auto it2 = it; it2 != s.end();it2++){
                        if(ta[*it2] <= se){
                            tmp.push_back(*it2);
                        }else{
                            break;
                        }
                    }
                    for(auto yy:tmp){
                        s.erase(yy);
                    }
                    s.insert(fi);
                    //cout<<"s: ";for(auto yy:s) cout<<yy<<" ";cout<<endl;
                    ta[fi] = se;
                    res.push_back(s.size());
                }else{
                    res.push_back(s.size());
                    continue;
                }
            }
        }
        return res;
    }
};
