class Solution {
public:

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        int ans = 0;
        vector<int> v;
        for(int i = 0; i < n; i++) s.insert(i);
        for(auto it : queries) {
            int l = it[0],r = it[1];

            vector<int> vv;
            auto it1 = s.lower_bound(l);
            while(it1 != s.end()) {
                int e = *it1;
                if(e >= r) break;
                if(e > l) vv.push_back(e);
                it1++;
            }
            for(int ch : vv) s.erase(ch);
            int res = s.size()-1;
            v.push_back(res);
        }
        return v;
    }
};
