class ListNode:
    def __init__(self, val=0, next=None):
        self.val = val
        self.next = next

    def __repr__(self):
        return f"{self.val}->{self.next}"

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        d = {}
        a = ListNode(n-1)
        d[n-1] = a
        for i in range(n-2, -1, -1):
            a = ListNode(i, a)
            d[i] = a
        res = []
        for i, j in queries:
            if i in d and j in d:
                l, r = d[i].next, d[j]
                while l != r:
                    del d[l.val]
                    l = l.next
                d[i].next = d[j]
            res.append(len(d)-1)
        return res
